import logo from './logo.svg';
import './App.css';
import * as React from 'react';
import ReactDOM from 'react-dom';
import Button from '@mui/material/Button';
import { TextField } from '@mui/material';
import { Container } from '@mui/material';
import { Alert } from '@mui/material';
import { Stack} from '@mui/material';
import { Transitions } from '@mui/material';


class guess extends React.Component{
  constructor(props){
    super(props);
    const num = Math.random()*100;
    this.state = {
      result: "",
      userinput: "",
    };

  }

  handleAnswer(){
    if(this.state.userinput > this.num){
      this.setState({
        result: "High"
      })
    }else if(this.state.userinput < this.num){
      this.setState({
        result: "Low"
      })
    }else{
      this.setState({
        result: "Correct"
      })
    }

  }

  render(){
    this.handleAnswer();
    let button= <Alert severity="info">{this.state.result}</Alert>

     return(
        <div>
          {button}
          <TextField className="outlined-number" 
          onInput={value => this.state.userinput = value}>
          </TextField>
        </div>
     )
  }
}


function App() {
  return (
    <div>
    <header>
      <h1>
        Guess a number between 0 and 100
        </h1>
        </header>

    <Container maxWidth="sm">
      <TextField
          id="outlined-number"
          label="Number"
          type="number"
          InputLabelProps={{
            shrink: true,
          }}
        /> 
    </Container>
    </div>
  );
}



export default App;
